import React from 'react'

const CardCategory = ({category}) => {

    return (
        <div class="max-w-sm rounded overflow-hidden shadow-lg flex-none mr-6 mt-2">
            <div class="px-6 py-4">
                <div class="font-bold text-xl mb-2">{category.categoryName}</div>
                <p class="text-gray-700 text-base">
                    {category.categoryDescription}
                </p>
            </div>
            <div class="px-6 py-4">
                {category.tags.map(tag => (
                    <span class="inline-block bg-gray-200 rounded-full px-3 py-1 text-sm font-semibold text-gray-700 mr-2">{tag}</span>
                ))}
                
            </div>
        </div>
    )
}

export default CardCategory
